"use strict";var mySwiper=new Swiper(".swiper-container",{loop:!0,autoplay:{delay:2e3},effect:"fade",pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});function getList(){$.ajax({url:"../lib/nav_top.json",dataType:"json",success:function(e){$(".nav-mid >ul").on({mouseenter:function(){return $(".nav-mid-box").stop().slideDown()},mouseleave:function(){return $(".nav-mid-box").stop().slideUp()}}).children("li").on("mouseover",function(){var n=$(this).index(),i=e[n].list,t="";i.forEach(function(n){t+='\n                <li>\n                  <div>\n                    <img src="'.concat(n.list_url,'" alt="">\n                  </div>\n                  <p class="title">').concat(n.list_name,'</p>\n                  <span class="price">').concat(n.list_price,"</span>\n                </li>\n              ")}),$(".nav-mid-box > ul").html(t)}),$(".nav-mid-box").on({mouseover:function(){$(this).finish().show()},mouseout:function(){$(this).finish().slideUp()}})}})}function getBannerList(){$.ajax({url:"../lib/banner.json",dataType:"json",success:function(e){console.log(e);$(".banner-left>ul").on({mouseenter:function(){return $(".banner-list").stop().show()},mouseleave:function(){return $(".banner-list").stop().hide()}}).children("li").on("mouseover",function(){var n=$(this).index(),i=e[n].list,t="";i.forEach(function(n){t+='\n              <li>\n              <img\n                src="'.concat(n.src,'"\n                alt="">\n              <span>').concat(n.title,"</span>\n            </li>\n              ")}),$(".banner-list>.banner-left-list>ul").html(t)}),$(".banner-list").on({mouseover:function(){$(this).finish().show()},mouseout:function(){$(this).finish().hide()}})}})}getList(),getBannerList();